mapper.insert:
INSERT INTO board(num, subject, reg_date, readcount, ref, re_step, re_level, content, ip, upload, memberEmail) VALUES(board_num_seq.nextval, #{subject}, sysdate, 0,
board_num_seq.nextval,
#{ref},
#{re_step}, #{re_level}, #{content, jdbcType=VARCHAR}, #{ip}, #{upload, jdbcType=VARCHAR},#{memberEmail, jdbcType=VARCHAR}
)

mapper.update:
UPDATE board
SET re_step = re_step + 1
WHERE ref=#{ref} AND re_step > #{re_step}

UPDATE board
SET subject=#{subject}, content=#{content}

	,upload=#{upload} 

WHERE num=#{num}
